      <Card.Img variant="top" src={item.posterUrl} />
      <Card.Body>
        <Card.Title>{props.title}</Card.Title>
        <Card.Text>{props.description}</Card.Text>



      </Card.Body>
    </Card>
  );
}

export default MovieCard;
