import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import NavDropdown from 'react-bootstrap/NavDropdown';

import React, { useState,useEffect } from "react";
import MovieCard from './MovieCard'




function BasicExample({movies}) {
  const [filteredList, setFilteredList] = useState(movies);
  const [selectedTitle, setSelectedTitle] = useState("");
  const filterByTitle = (filteredData) => {
    // Avoid filter for empty string
    if (!selectedTitle) {
      return filteredData;
    }
    const filteredMovies = filteredData.filter(
      (movie) => movie.title===selectedTitle
    );
    return filteredMovies;
  };
  const handleTitleChange = (event) => {
    setSelectedTitle(event.target.value);
  };

  useEffect(() => {

 const filteredData = filteredList.filter(movie=>
movie.to

  );

    setFilteredList(filteredData);
  }, [selectedTitle]);

     //const results = people.filter(person =>
      //person.toLowerCase().includes(searchTerm)
     // );
      //setSearchResults(results);
   // }, [searchTerm]);





  return (
    <div>
    <Navbar bg="dark" expand="lg">
      <Container fluid>
        <Navbar.Brand href="/"style={{color:"white"}}>Medi@Land</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              value={selectedTitle}
              onChange={handleTitleChange}
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>

          <NavDropdown title="Filter by Title" id="basic-nav-dropdown" value={selectedTitle} onSelect={handleTitleChange}>
              <NavDropdown.Item value="">All</NavDropdown.Item>
              <NavDropdown.Item value="Titanic">Titanic</NavDropdown.Item>
              <NavDropdown.Item value="The Godfather">The Godfather</NavDropdown.Item>
              <NavDropdown.Item >Another action </NavDropdown.Item>
              <NavDropdown.Item >Something</NavDropdown.Item>

            </NavDropdown>
          </Nav>

        </Navbar.Collapse>
      </Container>
    </Navbar>

    <div className='row justify-content-center'>
                {filteredList.map((item,index)=>{
                    return (
                      <>
                        <MovieCard
                        item={item}
                        key={index}
                        />


                       </> 
                    )



                }
                )}


            </div>

    </div>

  );
}

export default BasicExample;
