import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Form } from 'react-bootstrap';
import React, { useState,useEffect} from "react";



function BasicExample({movies}) {
  const [filteredList, setFilteredList] = useState(movies);
  const [selectedTitle, setSelectedTitle] = useState("");
  const filterByTitle = (filteredData) => {
    // Avoid filter for empty string
    if (!selectedTitle) {
      return filteredData;
    }

    const filteredMovies = filteredData.filter(
      (movie) => movie.title===selectedTitle
    );
    return filteredMovies;
  };

  // Update seletedTitle state
  const handleTitleChange = (event) => {
    setSelectedTitle(event.target.value);
  };
  useEffect(() => {
    var filteredData = filterByTitle(movies);

    setFilteredList(filteredData);
  }, [selectedTitle]);
  return (
    <div>
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand href="/">Medi@Land</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#action1">Home</Nav.Link>


          </Nav>
          <Form className="d-flex">
            <Form.Control
              className='form-control'
              value={selectedTitle}
              onChange={handleTitleChange}
              placeholder='Type to search...'
            />

          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>

    </div>
  );
}

export default BasicExample;
