import MovieCard from './MovieCard'
import { Button, Container, Form, Nav, Navbar } from "react-bootstrap";
import ReactStars from "react-rating-stars-component";







function MovieList({ movies, searchTitle, searchRate,setsearchTitle, setsearchRate}) {
  const ratingChanged = (newRating) => {
    setsearchRate(newRating);
  };

  return (
    <>
    <div>
    <Navbar bg="dark" expand="lg">
      <Container fluid>
        <Navbar.Brand href="/"style={{color:"white"}}>Medi@Land</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >

            <Form className="d-flex">
          <Form.Control
            type="search"
            placeholder="Search"
            className="me-2"
            aria-label="Search"
            onChange={(e) => setsearchTitle(e.target.value)}
          />
          <Button variant="outline-secondary">Search</Button>
        </Form>

          </Nav>


        </Navbar.Collapse>

      </Container>
    </Navbar>

    </div>

            <div className='row justify-content-center'>

            {movies .filter( (movie) => movie.title .toUpperCase() .includes(searchTitle.toUpperCase().trim()) 
            &&
            movie.rate >= searchRate).map((movie) => (

          <MovieCard 
          movie={movie} 
          key={movie.id} 
          />
        ))}



            </div>

        </>

  )
}

export default MovieList
