import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';



import MovieCard from './MovieCard'
import Filter from './Filter';




function BasicExample({movies}) {







  return (
    <div>
    <Navbar bg="dark" expand="lg">
      <Container fluid>
        <Navbar.Brand href="/"style={{color:"white"}}>Medi@Land</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              value={movies.selectedTitle}
              onChange={movies.handleTitleChange}
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>


          </Nav>

        </Navbar.Collapse>
      </Container>
    </Navbar>
    <div>Filter by Title :</div>
        <select
          id="title-input"
          value={movies.selectedTitle}
          onChange={movies.handleTitleChange}
        >
          <option value="">All</option>
          <option value="Titanic">Titanic</option>
          <option value="The Godfather">The Godfather</option>
          <option value="Schindler's List">Schindler's List</option>
          <option value="The Shawshank Redemption">The Shawshank Redemption</option>
          <option value="12 Angry Men">12 Angry Men</option>
          <option value="The Dark Knight">The Dark Knight</option>
          <option value="Pulp Fiction">Pulp Fiction</option>
        </select>

        <div>Filter by Rate</div>
        <div id="Title-options" onClick={movies.handleRateChange}>
        <div
          className={movies.selectedRate === 1 ? "active-option" : "filter-option"}
          id="1"
        >
          ⭐
        </div>
        <div
          className={movies.selectedRate === 2 ? "active-option" : "filter-option"}
          id="2"
        >
          ⭐⭐
        </div>
        <div
          className={movies.selectedRate === 3 ? "active-option" : "filter-option"}
          id="3"
        >
          ⭐⭐⭐
        </div>
        <div
          className={movies.selectedRate === 4 ? "active-option" : "filter-option"}
          id="4"
        >
         ⭐⭐⭐⭐
        </div>
        <div
          className={movies.selectedRate === 5 ? "active-option" : "filter-option"}
          id="5"
        >
          ⭐⭐⭐⭐⭐
        </div>
      </div>

    <div className='row justify-content-center'>
                {movies.filteredList.map((item,index)=>{
                    return (
                      <>
                        <MovieCard
                        item={item}
                        key={index}
                        />


                       </> 
                    )



                }
                )}


            </div>

    </div>

  );
}

export default BasicExample;
