import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import React, { useState,useEffect } from "react";




function BasicExample() {
   // List of all movies all the filters
   const [filteredList, setFilteredList] = useState(movies);
   // Selected Titlefilter
const [selectedTitle, setSelectedTitle] = useState("");
// Selected Rate filter
const [selectedRate, setSelectedRate] = useState();
const filterByTitle = (filteredData) => {
 // Avoid filter for empty string
 if (!selectedTitle) {
   return filteredData;
 }

 const filteredMovies = filteredData.filter(
   (movie) => movie.title===selectedTitle
 );
 return filteredMovies;
};

const filterByRate = (filteredData) => {
 // Avoid filter for null value
 if (!selectedRate) {
   return filteredData;
 }

 const filteredMovies = filteredData.filter(
   (movie) => movie.rate === selectedRate
 );
 return filteredMovies;
};

// Update seletedTitle state
const handleTitleChange = (event) => {
 setSelectedTitle(event.target.value);
};

// Toggle seletedRate state
const handleRateChange = (event) => {
 const inputRate = Number(event.target.id);

 if (inputRate === selectedRate) {
   setSelectedRate("");
 } else {
   setSelectedRate(inputRate);
 }
};

useEffect(() => {
 var filteredData = filterByTitle(movies);
 filteredData = filterByRate(filteredData);
 setFilteredList(filteredData);
}, [selectedTitle, selectedRate]);

  return (
    <div>
    <Navbar bg="dark" variant="ligt">
      <Container fluid>
        <Navbar.Brand href="/"style={{color:"white"}}>Medi@Land</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#action1"style={{color:"white"}}>Home</Nav.Link>
            <NavDropdown  title="Filter By Title" 
            id="title-input"
            value={selectedTitle}
            onChange={handleTitleChange}


            >
              <NavDropdown.Item value="">All</NavDropdown.Item>
              <NavDropdown.Item value="Titanic"> Titanic</NavDropdown.Item>
             <NavDropdown.Item value="The Godfather">The Godfather</NavDropdown.Item>


            </NavDropdown>

          </Nav>

        </Navbar.Collapse>
      </Container>
    </Navbar>

    </div>
  );
}

export default BasicExample;
